import React, { useState } from "react";
import {
  Form,
  FormGroup,
  FormLabel,
  FormRow,
  FormSubmit,
} from "../../../../../../components/form";
import axios from "axios";

export default function AddSection(props) {
  const [errorMessage, setErrorMessage] = useState("");
  const [fields, setFields] = useState({
    name: "",
    rate: 0,
  });

  async function handleChange(e) {
    setFields({
      ...fields,
      [e.target.name]: e.target.value,
    });
  }

  async function handleSubmit(e) {
    e.preventDefault();
    try {
      const newService = {
        name: fields.name,
        rate: fields.rate,
      };

      const response = await axios.post(
        `http://localhost:4090/api/users/${props.user.username}/services`,
        newService,
        {
          credentials: "same-origin",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${props.session.authToken}`,
          },
        }
      );
      // console.log("New services", response);
      let data = response.data;
      if (response.status !== 200 && response.status !== 201) {
        throw new Error(data.message);
      }

      newService.id = data.id;
      // console.log(newService)
      if (props.appendService) {
        props.appendService(newService);
      }

      setFields({
        ...fields,
        title: "",
        content: "",
      });
    } catch (err) {
      console.error(`Failed to add new service. ${err}`);
      setErrorMessage(err.message);
    }
  }

  return (
    <Form className='container-fluid' handleSubmit={handleSubmit}>
      {errorMessage && (
        <FormRow className='justify-content-center'>
          <div className='alert alert-danger' role='alert'>
            {errorMessage}
          </div>
        </FormRow>
      )}
      <FormRow>
        <FormGroup className='col-12 col-md'>
          <FormLabel for='servicesForm01' className='md-font-sm text-muted'>
            Service Name
          </FormLabel>
          <input
            id='servicesForm01'
            name='name'
            type='text'
            className='form-control'
            value={fields.name}
            onChange={handleChange}
            placeholder='Try "Health check-up"'
          />
        </FormGroup>
        <FormGroup className='col-12 col-md'>
          <FormLabel for='servicesForm02' className='md-font-sm text-muted'>
            Rate ($)
          </FormLabel>
          <input
            id='servicesForm02'
            name='rate'
            type='number'
            className='form-control'
            value={fields.rate}
            onChange={handleChange}
            placeholder='00.00'
            min='0'
            step='0.01'
          />
        </FormGroup>
        <FormGroup className='col-sm-auto align-self-end'>
          <FormSubmit>Add</FormSubmit>
        </FormGroup>
      </FormRow>
    </Form>
  );
}
