import React, { useEffect, useState } from "react";
import {
  MonthDate,
  SelectMonthByName,
  SelectYear,
} from "../../../../../../components/dates";
import {
  Form,
  FormGroup,
  FormLabel,
  FormLegend,
  FormRow,
  FormSubmit,
} from "../../../../../../components/form";
import { Col, FluidContainer, Row } from "../../../../../../components/layout";
import { Loader } from "../../../../../../components/loaders";
import {
  QualificationInput,
  Qualifications,
  Username,
} from "../../../../../../components/users";
import {
  TitleBar,
  TitleButton,
  Widget,
  WidgetBody,
  WidgetDropdown,
  WidgetDropdownItem,
  WidgetList,
  WidgetListItem,
} from "../../../../../../components/widgets";
import axios from "axios";

function DefaultDegreeItem(props) {
  return (
    <WidgetListItem>
      <FluidContainer>
        <Row>
          <Col className='md-font-sm text-center text-muted'>
            No education available
          </Col>
        </Row>
      </FluidContainer>
    </WidgetListItem>
  );
}

function DegreeItem(props) {
  async function handleDelete(e) {
    e.preventDefault();
    if (props.handleDelete) {
      props.handleDelete(props.degree.id);
    }
  }

  const username = props.degree.username;
  const currentUsername = Username({ user: props.session });
  const fromCurrentUser = currentUsername === username ? true : false;

  const fromDate = new Date(props.degree.fromDate);
  const toDate = new Date(props.degree.toDate);

  return (
    <WidgetListItem>
      <FluidContainer className='px-0'>
        <Row>
          <Col>
            <Row>
              <Col className='align-self-center'>
                <Row>
                  <Col className='text-truncate font-weight-bold'>
                    {props.degree.degree}
                  </Col>
                  <Col className='col-auto'>
                    <MonthDate date={fromDate} /> - <MonthDate date={toDate} />
                  </Col>
                </Row>
              </Col>
            </Row>
            <Row>
              <Col className='text-truncate'>{props.degree.university}</Col>
            </Row>
          </Col>
          <Col className='col-auto'>
            {fromCurrentUser && (
              <WidgetDropdown>
                <WidgetDropdownItem handleClick={handleDelete}>
                  Delete
                </WidgetDropdownItem>
              </WidgetDropdown>
            )}
          </Col>
        </Row>
      </FluidContainer>
    </WidgetListItem>
  );
}

function AddForm(props) {
  const today = new Date();

  const [errorMessage, setErrorMessage] = useState("");
  const [fields, setFields] = useState({
    degree: Qualifications.length ? Qualifications[0] : "",
    fromMonth: today.getMonth() + 1,
    fromYear: today.getFullYear(),
    toMonth: today.getMonth() + 1,
    toYear: today.getFullYear(),
    university: "",
  });

  async function handleChange(e) {
    setFields((prevFields) => {
      return {
        ...prevFields,
        [e.target.name]: e.target.value,
      };
    });
  }

  async function handleSubmit(e) {
    e.preventDefault();
    try {
      const fromDate = new Date(fields.fromYear, fields.fromMonth - 1);
      const toDate = new Date(fields.toYear, fields.toMonth - 1);

      const newDegree = {
        degree: fields.degree,
        fromDate: fromDate,
        toDate: toDate,
        university: fields.university,
      };

      const response = await axios.post(
        `http://10.225.77.149:4090/api/users/${props.user.username}/degrees`,
        newDegree,
        {
          credentials: "same-origin",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${props.session.authToken}`,
          },
        }
      );

      let data = response.data;
      if (response.status !== 200) {
        throw new Error(data.message);
      }

      if (props.appendDegree) {
        props.appendDegree(newDegree);
      }
    } catch (err) {
      console.error(
        `Failed to update education information for user- ${props.user.username}. ${err}`
      );
      setErrorMessage(err.message);
    }
  }

  return (
    <Form handleSubmit={handleSubmit}>
      {fields.errorMessage && (
        <FormRow className='justify-content-center'>
          <div className='alert alert-danger' role='alert'>
            {errorMessage}
          </div>
        </FormRow>
      )}
      <FormRow>
        <FormLabel for='educationForm01' className='col-sm-4'>
          Qualification
        </FormLabel>
        <FormGroup className='col-sm-8'>
          <QualificationInput
            id='educationForm01'
            className='form-control'
            name='degree'
            label='Degree'
            value={fields.degree}
            handleChange={handleChange}
            required={true}
          />
        </FormGroup>
      </FormRow>
      <FormRow>
        <FormLegend for='educationForm02' className='col-sm-4'>
          From Date
        </FormLegend>
        <FormGroup className='col-sm-4'>
          <SelectMonthByName
            id='educationForm02'
            className='form-control'
            name='fromMonth'
            label='From Month'
            shortForm={true}
            value={fields.fromMonth}
            handleChange={handleChange}
            required={true}
          />
        </FormGroup>
        <FormGroup className='col-sm-4'>
          <SelectYear
            id='educationForm03'
            className='form-control'
            name='fromYear'
            label='From Year'
            startYear={today.getFullYear() - 100}
            endYear={today.getFullYear()}
            value={fields.fromYear}
            handleChange={handleChange}
            required={true}
          />
        </FormGroup>
      </FormRow>
      <FormRow>
        <FormLegend for='educationForm04' className='col-sm-4'>
          To Date
        </FormLegend>
        <FormGroup className='col-sm-4'>
          <SelectMonthByName
            id='educationForm04'
            className='form-control'
            name='toMonth'
            label='To Month'
            shortForm={true}
            value={fields.toMonth}
            handleChange={handleChange}
            required={true}
          />
        </FormGroup>
        <FormGroup className='col-sm-4'>
          <SelectYear
            id='educationForm05'
            className='form-control'
            name='toYear'
            label='To Year'
            startYear={today.getFullYear() - 100}
            endYear={today.getFullYear()}
            value={fields.toYear}
            handleChange={handleChange}
            required={true}
          />
        </FormGroup>
      </FormRow>
