}

export default function EducationWidget(props) {
  const [editMode, setEditMode] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [degrees, setDegrees] = useState([]);

  useEffect(() => {
    async function initialize() {
      try {
        setIsLoading(true);
        const response = await axios.get(
          `http://10.225.77.149:4090/api/users/${props.user.username}/degrees`,
          {
            credentials: "same-origin",
            headers: {
              Authorization: `Bearer ${props.session.authToken}`,
            },
          }
        );

        let data = response.data;
        if (response.status !== 200) {
          throw new Error(data.message);
        }

        setDegrees((prevDegrees) => {
          return [...prevDegrees, ...data];
        });
      } catch (err) {
        console.error(
          `Failed to get education information for user- ${props.user.username}. ${err}`
        );
      } finally {
        setIsLoading(false);
      }
    }

    initialize();
  }, [props.user, props.session]);

  async function toggleEditMode(e) {
    e.preventDefault();
    setEditMode(!editMode);
  }

  async function appendDegree(newDegree) {
    setDegrees((prevDegrees) => {
      return [...prevDegrees, newDegree];
    });
    setEditMode(false);
  }

  return (
    <Widget>
      <TitleBar title='Education'>
        {!props.disableEdit &&
          (editMode ? (
            <TitleButton
              name='Cancel'
              icon='clear'
              handleClick={toggleEditMode}
            />
          ) : (
            <TitleButton name='Add' icon='add' handleClick={toggleEditMode} />
          ))}
      </TitleBar>
      {editMode && (
        <WidgetBody>
          <AddForm
            session={props.session}
            user={props.user}
            appendDegree={appendDegree}
          />
        </WidgetBody>
      )}
      <WidgetBody>
        {isLoading ? (
          <Loader isLoading={true} />
        ) : (
          <WidgetList>
            {degrees.length !== 0 ? (
              <>
                {degrees.map((degree, index) => (
                  <DegreeItem
                    key={index}
                    session={props.session}
                    degree={degree}
                  />
                ))}
              </>
            ) : (
              <DefaultDegreeItem />
            )}
          </WidgetList>
        )}
      </WidgetBody>
    </Widget>
  );
}
